    .uint8('ShuntSOC', { formatter: (x) => { return x / 2 - 5; } })    // percent
    .int16le('ShuntVoltage', { formatter: (x) => { return x / 100; } })
    .floatle('ShuntCurrent', { formatter: (x) => { return x / 1000; } })
